import React, { useEffect, useState } from 'react'
import { SafeAreaView, Text, TextInput, StyleSheet, View, TouchableOpacity, KeyboardAvoidingView } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons'; 
import axios from 'axios';


export default function TodoApp() {
  const [name, setName] = useState("");
  const [todo, setTodo] = useState([])

  const handleSubmit = () => {
    const data = {
      name: name,
    }
    console.log(data);
    axios.post('http://192.168.100.71:5000/api/v1/todo', data)
    .then(res => {
      setName("")
      console.log('res', res);
    })
  }

  const getData = () =>{
    axios.get('http://192.168.100.71:5000/api/v1/todos')
    .then(res=>{
      console.log('res', res);
      setTodo('res', res)
    })
  }

  useEffect(()=>{
    getData()
  }, [])

  return (
    <SafeAreaView style={styles.containerMain}>
        {
        todo.map((item, index) => {
          return (
            <View style={styles.container}>
            <View style={styles.indexContainer}>
                <Text style={styles.index}>1</Text>
            </View>
