                    <MaterialIcons style={styles.delete} name="delete" size={18} color='#fff' />
                </TouchableOpacity>
            </View>
        </View>
          );
        })
      }
        <View style={styles.containerInput}>
        <TextInput style={styles.inputField} value={name} onChangeText={(value) => setName(value)}/>
        <TouchableOpacity onPress={handleSubmit}>
          <View style={styles.button}>
              <MaterialIcons name="keyboard-arrow-up" size={24} color="black" />
          </View>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  containerMain: {
    flex: 1,
    backgroundColor: '#FF7878',
  },
  container: {
      flexDirection: 'row',
      marginHorizontal: 20,
      marginTop: 20
  },
  indexContainer: {
      backgroundColor: '#BFFFF0',
      borderRadius: 12,
      marginRight: 10,
      alignItems: 'center',
      justifyContent: 'center',
      width: 50,
      height: 50,
  },
  index: {
      color: '#FF7878',
      fontSize: 20,
  },
  taskContainer: {
      backgroundColor: '#BFFFF0',
      borderRadius: 12,
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      flex: 1,
      paddingHorizontal: 10,
      paddingVertical: 5,
      minHeight: 50,
  },
  task: {
      color: '#FF7878',
      width: '90%',
      fontSize: 16,
  },
  delete: {
      marginLeft: 10,
      color: '#FF7878'
  },
  inputField: {
    color: '#fff',
    height: 50,
    margin: 10,
    flex:1
  },
  button: {
      height: 30,
      width: 30,
      borderRadius: 5,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center'
  },
  containerInput: {
    width: '90%',
    borderColor: '#fff',
    borderWidth: 1,
    marginHorizontal: 20,
    borderRadius: 12,
    flexDirection: 'row',
    alignItems: 'center',
