import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  Image,
  Dimensions,
  ScrollView,
} from 'react-native';



const Home = () => {

  return (
    <View style={styles.container}>
      <View style={styles.contentContainer}>
        <Text style={styles.title}>Home</Text>
      </View>
{/* Scrollable Content */}
      <View>
        <Image source={require('../../assets/Home.png')} />
      </View>
    </View>
  );
};
export default Home;

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#FF7878',
    },
    contentContainer: {
      marginTop: 50,
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingBottom: 20,
    },
    title: {
      fontSize: 20,
      color: '#fff',
    },
    scrollContainer: {
      flex: 1,
    }
  });
