import { AntDesign } from '@expo/vector-icons'
import { StyleSheet, Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Home from './src/screens/Home'
import TodoApp from './src/screens/TodoApp'

export default function App() {
  const Tab = createBottomTabNavigator();
  const IconBottom = (props) => {
    const { color, focused } = props.data
    let colorSelected = focused ? color : 'grey'
    return (
        <View>
            <Image source={props.image} style={{ width: 40, height: 40, tintColor: colorSelected }} />
        </View>
    )
}

  return (
  <NavigationContainer>
    <Tab.Navigator>
      <Tab.Screen
      options={{
        title: "Home",
        tabBarIcon: (props) => (
        <IconBottom data={props} image={require('./assets/sunny.gif')} />
        )}} 
      name="Home" 
      component={Home} />
      <Tab.Screen name="TodoApp" component={TodoApp} />
    </Tab.Navigator>
  </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
