import React, { useEffect, useState } from 'react'
import { SafeAreaView, Text, TextInput, StyleSheet, View, TouchableOpacity, KeyboardAvoidingView } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons'; 
import axios from 'axios';

const Data = ({name, index, onDelete, onEdit}) =>{
  return(
        <View style={styles.container}>
            <View style={styles.indexContainer}>
                <Text style={styles.index}>{index + 1}</Text>
            </View>
            <View style={styles.taskContainer}>
                <Text style={styles.task}>{name}</Text>
                <TouchableOpacity onPress={onEdit}>
                    <MaterialIcons style={styles.delete} name="edit" size={18} color='#fff' />
                </TouchableOpacity>
                <TouchableOpacity onPress={onDelete}>
                    <MaterialIcons style={styles.delete} name="delete" size={18} color='#fff' />
                </TouchableOpacity>
            </View>
        </View>
  )
}

export default function TodoApp() {
  const [name, setName] = useState("");
  const [todo, setTodo] = useState([])
  
  useEffect(()=>{
    getData()
  }, [])

  const handleSubmit = () => {
    const data = {
      name: name,
    }
    console.log(data);
    axios.post('http://192.168.100.71:5000/api/v1/todo', data)
    .then(res => {
      setName("")
      // console.log('res', res);
    })
  }

  const getData = () =>{
    axios.get('http://192.168.100.71:5000/api/v1/todos')
    .then(res=>{
      // console.log('res', res.data.data);
      setTodo(res.data.data)
    })
  }

  const editData = (data) =>{
    console.log(data);
    setName(data)
    // axios.patch(`http://192.168.100.71:5000/api/v1/todo/${data.id}`)
    // .then(res=>{
    //   getData()
    // })
  }
  const deleteData = (data) =>{
    axios.delete(`http://192.168.100.71:5000/api/v1/todo/${data.id}`)
    .then(res=>{
      getData()
    })
  }


  return (
    <SafeAreaView style={styles.containerMain}>
        {todo.map((item, index)=>{
          return <Data 
          key={item.id} 
          index={index} 
          name={item.name}
          onDelete={()=>deleteData(item)}
          onEdit={()=>editData(item)}/>
        })}
        <View style={styles.containerInput}>
        <TextInput style={styles.inputField} value={name} onChangeText={(value) => setName(value)}/>
        <TouchableOpacity onPress={handleSubmit}>
          <View style={styles.button}>
              <MaterialIcons name="keyboard-arrow-up" size={24} color="black" />
          </View>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  containerMain: {
    flex: 1,
    backgroundColor: '#FF7878',
  },
  container: {
      flexDirection: 'row',
      marginHorizontal: 20,
      marginTop: 20
  },
  indexContainer: {
      backgroundColor: '#BFFFF0',
      borderRadius: 12,
      marginRight: 10,
      alignItems: 'center',
      justifyContent: 'center',
      width: 50,
      height: 50,
  },
  index: {
      color: '#FF7878',
      fontSize: 20,
  },
  taskContainer: {
      backgroundColor: '#BFFFF0',
      borderRadius: 12,
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      flex: 1,
      paddingHorizontal: 10,
      paddingVertical: 5,
      minHeight: 50,
  },
  task: {
      color: '#FF7878',
      width: '90%',
      fontSize: 16,
  },
  delete: {
      marginLeft: 10,
      color: '#FF7878'
  },
  inputField: {
    color: '#fff',
    height: 50,
    margin: 10,
    flex:1
  },
  button: {
      height: 30,
      width: 30,
      borderRadius: 5,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center'
  },
  containerInput: {
    width: '90%',
    borderColor: '#fff',
    borderWidth: 1,
    marginHorizontal: 20,
    borderRadius: 12,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 5,
    position: 'absolute',
    bottom: 20,
},
});
